namespace MageINIC\Faq\Model\Source\Faq;

use Magento\Framework\Option\ArrayInterface;

/**
 * Faq Class Visibility
 */
class Visibility implements ArrayInterface
{
    public const VISIBILITY_PUBLIC = 0;
    public const VISIBILITY_FOR_LOGGED_IN_ONLY = 1;

    /**
     * Option Array
     *
     * @return array
     */
    public function toOptionArray(): array
    {
        $options = [];
        foreach ($this->toArray() as $value => $label) {
            $options[] = [
                'value' => $value,
                'label' => $label
            ];
        }

        return $options;
    }

    /**
     * Get options in "key-value" format
     *
     * @return array
     */
    public function toArray(): array
    {
        return [
            self::VISIBILITY_PUBLIC  => __('Public'),
            self::VISIBILITY_FOR_LOGGED_IN_ONLY  => __('For logged in only'),
        ];
    }
}
